#!/bin/bash
RED="[0;31m"; GREEN="[0;32m"; BLUE="[0;34m"; NC="[0m"; save_history() { local file=$1; local timestamp=$(date +%Y%m%d_%H%M%S); local history_dir=".history/${file%.*}"; mkdir -p "$history_dir"; cp "$file" "${history_dir}/${file%.*}_${timestamp}.${file##*.}"; echo -e "${GREEN}‚úÖ –°–æ—Ö—Ä–∞–Ω–µ–Ω–∞ –∏—Å—Ç–æ—Ä–∏—è –¥–ª—è $file${NC}"; }; echo "üîÑ –°–æ—Ö—Ä–∞–Ω—è—é –∏—Å—Ç–æ—Ä–∏—é –∫–ª—é—á–µ–≤—ã—Ö —Ñ–∞–π–ª–æ–≤..."; for file in MAIN.md ROADMAP.md SELF_DIAGNOSIS.md; do [ -f "$file" ] && save_history "$file" || echo -e "${RED}‚ùå –§–∞–π–ª $file –Ω–µ –Ω–∞–π–¥–µ–Ω${NC}"; done
